import React from "react";
import componentStyle from "./index.module.css";

export default class MultiphysicsNumericalAlgorithms extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isUnfold: [false, false, false, false, false, false, false, false],
    };
  }

  content = [
    "区域分解(DDM)",
    "预条件(RC)",
    "SG算法",
    "牛顿迭代算法",
    "控制体积有限元(CV-FEM)",
    "迎风格式有限元(SUPG-FEM)",
    "时域有限元(FETD)",
    "时域有限差分(FDTD)",
  ];

  render() {
    return (
      <div className={componentStyle.container}>
        <div className={componentStyle.title}>多物理场数值算法</div>
        <div className={componentStyle.select}>
          {this.content.map((value, index) => {
            return (
              <div
                key={value}
                onClick={(e) => {
                  switch (e.currentTarget.innerText) {
                    case this.content[0]:
                      this.setState({
                        isUnfold: [
                          true,
                          false,
                          false,
                          false,
                          false,
                          false,
                          false,
                          false,
                        ],
                      });
                      break;
                    case this.content[1]:
                      console.log("2");
                      break;
                    case this.content[2]:
                      console.log("3");
                      break;
                    case this.content[3]:
                      console.log("4");
                      break;
                    case this.content[4]:
                      console.log("5");
                      break;
                    case this.content[5]:
                      console.log("6");
                      break;
                    case this.content[6]:
                      console.log("7");
                      break;
                    case this.content[7]:
                      console.log("8");
                      break;
                    default:
                      break;
                  }
                }}
              >
                <span>{value}</span>
              </div>
            );
          })}
        </div>
      </div>
    );
  }
}
