import React from "react";
import componentStyle from "./index.module.css";
import ArrowBtn from "./component/ArrowBtn";

export default class MainModule extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      moduleIndex: [1, 2, 3, 4, 5],
    };
    this.moduleStyle = {
      1: this.firstStyle,
      2: this.secondStyle,
      3: this.thirdStyle,
      4: this.forthStyle,
      5: this.fifthStyle,
    };
  }

  firstStyle = {
    backgroundColor: "rgba(255, 255, 255, 0.3)",
    backdropFilter: "blur(10px)",
    width: "10%",
    height: "60%",
    left: "-10%",
    top: "20%",
    transition: "all 1s linear",
    visibility: "hidden",
  };

  secondStyle = {
    backgroundColor: "rgba(255, 255, 255, 0.3)",
    backdropFilter: "blur(10px)",
    width: "20%",
    height: "80%",
    left: "2.5%",
    top: "10%",
    transition: "all 1s linear",
  };

  thirdStyle = {
    backgroundColor: "rgba(255, 255, 255, 0.3)",
    backdropFilter: "blur(10px)",
    width: "50%",
    height: "100%",
    left: "25%",
    top: 0,
    transition: "all 1s linear",
  };

  forthStyle = {
    backgroundColor: "rgba(255, 255, 255, 0.3)",
    backdropFilter: "blur(10px)",
    width: "20%",
    height: "80%",
    left: "77.5%",
    top: "10%",
    transition: "all 1s linear",
  };

  fifthStyle = {
    backgroundColor: "rgba(255, 255, 255, 0.3)",
    backdropFilter: "blur(10px)",
    width: "10%",
    height: "60%",
    left: "100%",
    top: "20%",
    transition: "all 1s linear",
    visibility: "hidden",
  };

  render() {
    console.log("render!");
    return (
      <div className={componentStyle.container}>
        <div className={componentStyle["modules"]}>
          <div style={this.moduleStyle[this.state.moduleIndex[0]]}></div>
          <div style={this.moduleStyle[this.state.moduleIndex[1]]}></div>
          <div style={this.moduleStyle[this.state.moduleIndex[2]]}></div>
          <div style={this.moduleStyle[this.state.moduleIndex[3]]}></div>
          <div style={this.moduleStyle[this.state.moduleIndex[4]]}></div>
        </div>
        <div className={componentStyle.leftArrowBtn} onClick={this.goLeftPage}>
          <ArrowBtn />
        </div>
        <div
          className={componentStyle.rightArrowBtn}
          onClick={this.goRightPage}
        >
          <ArrowBtn />
        </div>
      </div>
    );
  }

  goLeftPage = () => {
    console.log(this.state.moduleIndex);
    this.setState({
      moduleIndex: this.state.moduleIndex.map((idx) => {
        if (idx !== 5) {
          return ++idx;
        } else {
          return 1;
        }
      }),
    });
    console.log(this.state.moduleIndex);
  };

  goRightPage = () => {
    console.log(this.state.moduleIndex);
    this.setState({
      moduleIndex: this.state.moduleIndex.map((idx) => {
        if (idx !== 1) {
          return --idx;
        } else {
          return 5;
        }
      }),
    });
    console.log(this.state.moduleIndex);
  };
}
