import React from "react";
import componentStyle from "./index.module.css";
import ArrowBtn from "./component/ArrowBtn";

export default class MainModule extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      moduleIndex: [1, 2, 3, 4, 5],
      showNext: this.props.showNext,
    };
    this.moduleStyle = {
      1: this.firstStyle,
      2: this.secondStyle,
      3: this.thirdStyle,
      4: this.forthStyle,
      5: this.fifthStyle,
    };
  }

  firstStyle = {
    backgroundColor: "rgba(255, 255, 255, 0.3)",
    backdropFilter: "blur(10px)",
    width: "10%",
    height: "60%",
    left: "-10%",
    top: "20%",
  };

  secondStyle = {
    backgroundColor: "rgba(255, 255, 255, 0.3)",
    backdropFilter: "blur(10px)",
    width: "20%",
    height: "80%",
    left: "2.5%",
    top: "10%",
  };

  thirdStyle = {
    backgroundColor: "rgba(255, 255, 255, 0.3)",
    backdropFilter: "blur(10px)",
    width: "50%",
    height: "100%",
    left: "25%",
  };

  forthStyle = {
    backgroundColor: "rgba(255, 255, 255, 0.3)",
    backdropFilter: "blur(10px)",
    width: "20%",
    height: "80%",
    right: "2.5%",
    top: "10%",
  };

  fifthStyle = {
    backgroundColor: "rgba(255, 255, 255, 0.3)",
    backdropFilter: "blur(10px)",
    width: "10%",
    height: "60%",
    right: "-10%",
    top: "20%",
  };

  render() {
    return (
      <div>
        <div className={componentStyle.container}>
          <div style={this.moduleStyle[this.state.moduleIndex[0]]}></div>
          <div style={this.moduleStyle[this.state.moduleIndex[1]]}></div>
          <div style={this.moduleStyle[this.state.moduleIndex[2]]}></div>
          <div style={this.moduleStyle[this.state.moduleIndex[3]]}></div>
          <div style={this.moduleStyle[this.state.moduleIndex[4]]}></div>
        </div>
        <div className={componentStyle.leftArrowBtn}>
          <ArrowBtn />
        </div>
        <div className={componentStyle.rightArrowBtn}>
          <ArrowBtn />
        </div>
      </div>
    );
  }
}
